$("#cp_submit").submit(function(e){e.preventDefault();$.ajaxSetup({headers:{"X-CSRF-TOKEN":$('meta[name="_token"]').attr("content")}});$.ajax({type:"POST",url:"/retrieve_weather",data:$(this).serialize(),success:function(response){var data_response=JSON.parse(response);console.log(data_response)}})});$("#cp_input").keyup(function(){let length_num=$(this).val().length;if(length_num>5){$(this).val($(this).val().slice(0,-1))}});